import React from 'react';
import { Tag } from 'primereact/tag';
import { useState } from 'react';


// import { Container } from './styles';
interface Props {
    data: any
  }
interface Select{
    name: string;
    value: boolean;
}
const Tags: React.FC<Props> = (props:Props) => {
    const [selected, setSelected] = useState<Select[]>([]);

    props.data.map((c:string) =>{
        selected.push(  {name: c, value: false });
    });
    setSelected(selected);

  return   <div className="content-section introduction"><div>  <div className="card">{
    props.data.map((tag: any, index: number) =>{
        return <Tag value={tag} title={tag} className="mr-2 mt-2" icon="pi pi-plus" severity={ (selected.find( x=> x.name == tag)?.value) ? 'success': 'warning' } onClick={(e:any) => {
            console.log(e);
            console.log(e.target.innerHTML);

             if(selected.find( x=> x.name == e.target.innerHTML)?.value == false ){
                    selected.push(  {name: e.target.innerHTML, value: true });
                    setSelected(selected);
                    console.log(selected);
             }
             else{
                selected.find( x=> x.name == e.target.innerHTML)!.value = false;
                setSelected(selected );
                console.log(selected);
             }
        }}></Tag>
    })
    } </div></div></div>;
}

export default Tags;
