import axios from "axios";
import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import Tags from "./controls/Tags";
import { } from "primereact/blockui";
import { DataView, DataViewLayoutOptions, DataViewLayoutType } from 'primereact/dataview';
import { Tag } from "primereact/tag";

interface Props {
  // Define the props interface here
}


interface Post {
  id: string;
  title: string;
  publishedDate: string;
  categories: Categories[];
  author: Author;
}

interface Author {
  name: string;
  avatar: string;
}

interface Categories {
  id: string;
  name: string;
}

const App: React.FC<Props> = () => {


  const [data, setData] = useState<any>([]);
  const [categories, setCategories] = useState<any>([]);
  const [posts, setPosts] = useState<any>();
  const [layout, setLayout] = useState('grid');

  useEffect(() => {
    const getData = async () => {
      let response = await axios.get("/api/posts/");

      console.log("Data: " + JSON.stringify(response));
      console.log("Posts: " + JSON.stringify(response.data["posts"]));

      let posts: any = response.data["posts"];
      let pst: Post[] = [];
      posts.map((post: Post) => {
        //debugger;
        pst.push(post);
      });
      let cats: any = [];
      debugger;
      setPosts(pst);
      debugger;
      posts.map((post: Post) => {

        let ccat = post.categories;
        ccat.map((cat: Categories) => {
          var findItem = cats.find((x: string) => x === cat.name);
          if (!findItem) cats.push(cat.name);
        });

        console.log("Categories raw: " + JSON.stringify(post));
        console.log("Categories: " + cats);

        setCategories(cats);

      });

    };

    getData();
  }, []);

  const renderListItem = (data: any) => {
    return (
      <div className="col-12">
        <div className="product-list-item">
          <img src={`images/product/${data.author.avatar}`} onError={(e: any) => e.target.src = 'https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png'} alt={data.name} />
          <div className="product-list-detail">
            <div className="product-name">{data.title}</div>
            <div className="product-name">{data.author.name}</div>
            <div className="product-description">{data.summary}</div>
            {/* <Rating value={data.rating} readOnly cancel={false}></Rating> */}
            <i className="pi pi-tag product-category-icon"></i><span className="product-category">
              {data.categories.map((ct: Categories) => {

                return (<Tag value={ct.name} className="mr-2 mb-2" />)

              })}
            </span>
          </div>
          <div className="product-list-action">
            {/* <span className="product-price">${data.price}</span> */}
            {/* <Button icon="pi pi-shopping-cart" label="Add to Cart" disabled={data.inventoryStatus === 'OUTOFSTOCK'}></Button> */}
            {/* <span className={`product-badge status-${data.inventoryStatus.toLowerCase()}`}>{data.inventoryStatus}</span> */}
          </div>
        </div>
      </div>
    );
  }

  const renderGridItem = (data: any) => {
    return (
      <div className="col-12 md:col-4">
        <div className="product-grid-item card">
          <div className="product-grid-item-top">
            <div>
              <i className="pi pi-tag product-category-icon"></i>
              <span className="product-category">{data.categories.map((ct: Categories) => {

                return (<Tag value={ct.name} className="mr-2 mb-2" />)

              })}</span>
            </div>
            {/* <span className={`product-badge status-${data.inventoryStatus.toLowerCase()}`}>{data.inventoryStatus}</span> */}
          </div>
          <div className="product-grid-item-content">
            <img src={`images/product/${data.author.avatar}`} onError={(e: any) => e.target.src = 'https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png'} alt={data.name} />
            <div className="product-name">{data.title}</div>
            <div className="product-name">{data.author.name}</div>
            <div className="product-description">{data.summary}</div>
            {/* <Rating value={data.rating} readOnly cancel={false}></Rating> */}
          </div>
          <div className="product-grid-item-bottom">
            {/* <span className="product-price">${data.price}</span> */}
            {/* <Button icon="pi pi-shopping-cart" label="Add to Cart" disabled={data.inventoryStatus === 'OUTOFSTOCK'}></Button> */}
          </div>
        </div>
      </div>
    );
  }

  const itemTemplate = (posts: any, layout: any) => {
    if (!posts) {
      return;
    }

    if (layout === 'list')
      return renderListItem(posts);
    else if (layout === 'grid')
      return renderGridItem(posts);
  }

  const renderHeader = () => {
    return (
      <div className="grid grid-nogutter">

        <div className="col-6" style={{ textAlign: 'right' }}>
          <DataViewLayoutOptions layout={layout as (DataViewLayoutType)} onChange={(e) => setLayout(e.value)} />
        </div>
      </div>
    );
  }

  return <div>

    <div className="content-section introduction"> <Tags data={categories} />
      <div className="card">
        <div className="row">
          <DataView value={posts} layout={layout as (DataViewLayoutType)} header={renderHeader()}
            itemTemplate={itemTemplate} paginator rows={9}
          // sortOrder={sortOrder} sortField={sortField} 
          />
        </div>
      </div>
    </div>
  </div>;
}

export default App;
